    render() {
        return (
            <div>
                <div>
                    <input
                        type="text"
                        value={this.props.inputValue}
                        onChange={this.props.changeInputValue}
                    />
                    <button onClick={this.props.handleChick}>提交</button>
                </div>
                <ul>
                    {
                        this.props.list.map((item,index) => {
                            return <li
                                key={index}
                                onClick={(index) => this.props.handleItemDelete(index)}
