}
//store.dispatch, props
const mapDispatchToProps = (dispatch) => {
    return {
        changeInputValue(e) {
            const action = {
                type: 'change_input_value',
                value: e.target.value
            }
            dispatch(action);
        },
        handleChick() {
            const action = {
                type: 'add_item'
            }
            dispatch(action);
        },
        handleItemDelete(idx) {
            const action = {
                type: 'delete_item',
                index: idx
            }
            dispatch(action)
        }
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(TodoList);
