    render() {
        return (
            <div>
                <div>
                    <input
                        type="text"
                        value={this.props.inputValue}
                        onChange={this.handleInputChange.bind(this)}
                    />
                    <button>提交</button>
                </div>
                <ul>
                    <li>Dell</li>
                </ul>
            </div>
        )
    }
}
const  mapStateToProps = (state) => { //将reducer中的state映射到props里
    return {
       inputValue: state.inputValue
    }
}

export default connect(mapStateToProps,null)(TodoList);
